pragma solidity 0.7.6;
import {ERC20} from '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import {SignedSafeMath} from '@openzeppelin/contracts/math/SignedSafeMath.sol';
import {SafeCast} from '@openzeppelin/contracts/utils/SafeCast.sol';

import {IBabController} from '../interfaces/IBabController.sol';
import {IPriceOracle} from '../interfaces/IPriceOracle.sol';
import {IIntegration} from '../interfaces/IIntegration.sol';
import {IStrategy} from '../interfaces/IStrategy.sol';
import {IGarden} from '../interfaces/IGarden.sol';
import {IBaseIntegration} from '../interfaces/IBaseIntegration.sol';

import {LowGasSafeMath} from '../lib/LowGasSafeMath.sol';
import {PreciseUnitMath} from '../lib/PreciseUnitMath.sol';

/**
 * @title BaseIntegration
 * @author Babylon Finance
 *
 * Abstract class that houses common Integration-related state and functions.
 */
abstract contract BaseIntegration is IBaseIntegration {
    using SafeCast for int256;
    using LowGasSafeMath for uint256;
    using SignedSafeMath for int256;
    using PreciseUnitMath for uint256;

    /* ============ Modifiers ============ */

    modifier onlySystemContract() {
        require(controller.isSystemContract(msg.sender), 'Only system can call this');
        _;
    }

    /* ============ Constants ============ */

    address internal constant WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address internal constant DAI = 0x6B175474E89094C44Da98b954EedeAC495271d0F;
    address internal constant WBTC = 0x2260FAC5E5542a773Aa44fBCfeDf7C193bc2C599;
    address internal constant USDC = 0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48;
    address internal constant USDT = 0xdAC17F958D2ee523a2206206994597C13D831ec7;
    address internal constant ETH_ADD_CURVE = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
    address internal constant SNX = 0xC011a73ee8576Fb46F5E1c5751cA3B9Fe0af2a6F;
    address internal constant sETH = 0x5e74C9036fb86BD7eCdcb084a0673EFc32eA31cb;
    address internal constant sUSD = 0x57Ab1ec28D129707052df4dF418D58a2D46d5f51;
    address internal constant AAVE = 0x7Fc66500c84A76Ad7e9c93437bFc5Ac33E2DDaE9;

    /* ============ State Variables ============ */

    // Address of the controller
    IBabController public immutable controller;

    // Name of the integration
    string public override name;

    /* ============ Constructor ============ */

    /**
     * Creates the integration
     *
     * @param _name                   Name of the integration
     * @param _controller             Address of the controller
     */

    constructor(string memory _name, IBabController _controller) {
        require(address(_controller) != address(0), 'Controller must be defined');
        name = _name;
        controller = _controller;
    }

    /* ============ External Functions ============ */

    /* ============ Internal Functions ============ */

    function _getTokenOrETHBalance(address _strategy, address _token) internal view returns (uint256) {
        if (_token == address(0) || _token == ETH_ADD_CURVE) {
            return _strategy.balance;
        }
        return ERC20(_token).balanceOf(_strategy);
    }

    function _getDurationStrategy(address _strategy) internal view returns (uint256) {
        IStrategy strategy = IStrategy(_strategy);
        (, , , , uint256 executedAt, , ) = strategy.getStrategyState();
        return block.timestamp.sub(executedAt);
    }

    function _getRemainingDurationStrategy(address _strategy) internal view returns (uint256) {
        IStrategy strategy = IStrategy(_strategy);
        (, , , , uint256 executedAt, , ) = strategy.getStrategyState();
        uint256 runningFor = block.timestamp.sub(executedAt);
        if (runningFor > strategy.duration()) {
            return 0;
        }
        return strategy.duration().sub(runningFor);
    }

    function _getPrice(address _tokenIn, address _tokenOut) internal view returns (uint256) {
        IPriceOracle oracle = IPriceOracle(IBabController(controller).priceOracle());
        return oracle.getPrice(_tokenIn, _tokenOut);
    }

    function _getTradeCallData(
        address _strategy,
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn,
        uint256 _minAmountOut
    )
        internal
        view
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        bytes memory methodData =
            abi.encodeWithSignature(
                'trade(address,address,uint256,address,uint256)',
                _strategy,
                _tokenIn,
                _amountIn,
                _tokenOut,
                _minAmountOut
            );
        return (controller.masterSwapper(), 0, methodData);
    }
}
","keccak256":"0x631d1807f29dbe2076f9c82e3f390c5b95c90f7665a722a3e05d4cdf2a516cdc","license":"Apache-2.0"},"contracts/integrations/trade/CurveTradeIntegration.sol":{"content":"// SPDX-License-Identifier: Apache-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import {IBabController} from '../../interfaces/IBabController.sol';
import {IStrategy} from '../../interfaces/IStrategy.sol';
import {ICurveMetaRegistry} from '../../interfaces/ICurveMetaRegistry.sol';

import {LowGasSafeMath as SafeMath} from '../../lib/LowGasSafeMath.sol';
import {ControllerLib} from '../../lib/ControllerLib.sol';

import {TradeIntegration} from './TradeIntegration.sol';

/**
 * @title CurveTradeIntegration
 * @author Babylon Finance Protocol
 *
 * Curve trade integration
 */
contract CurveTradeIntegration is TradeIntegration {
    using SafeMath for uint256;
    using ControllerLib for IBabController;

    /* ============ Modifiers ============ */

    /* ============ State Variables ============ */

    /* ============ Constants ============ */

    ICurveMetaRegistry private immutable curveMetaRegistry;

    /* ============ Constructor ============ */

    /**
     * Creates the integration
     *
     * @param _controller                   Address of the controller
     * @param _curveMetaRegistry            Address of the curve meta registry
     */
    constructor(IBabController _controller, ICurveMetaRegistry _curveMetaRegistry)
        TradeIntegration('curve_trade', _controller)
    {
        require(address(_curveMetaRegistry) != address(0), 'Address needs to be valid');
        curveMetaRegistry = _curveMetaRegistry;
    }

    /* ============ Internal Functions ============ */

    /**
     * Executes the trade through curve.
     *
     * hparam _strategy             Address of the strategy
     * @param _sendToken            Address of the token to be sent to the exchange
     * @param _sendQuantity         Units of reserve asset token sent to the exchange
     * @param _receiveToken         Address of the token that will be received from the exchange
     */
    function _getTradeCallData(
        address, /* _strategy */
        address _sendToken,
        uint256 _sendQuantity,
        address _receiveToken
    )
        internal
        view
        override
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        (address curvePool, address realSendToken, address realReceiveToken) =
            _getPoolAndTokens(_sendToken, _receiveToken);
        require(curvePool != address(0), 'No curve pool');
        (uint256 i, uint256 j, bool underlying) =
            curveMetaRegistry.getCoinIndices(curvePool, realSendToken, realReceiveToken);
        // Palstakeaave. TODO: Add others
        bytes memory methodData;
        // palStkAAVE/AAVE pool
        if (curvePool == 0x48536EC5233297C367fd0b6979B75d9270bB6B15) {
            methodData = abi.encodeWithSignature('exchange(uint256,uint256,uint256,uint256)', i, j, _sendQuantity, 1);
        } else {
            methodData = abi.encodeWithSignature(
                'exchange(int128,int128,uint256,uint256)',
                int128(i),
                int128(j),
                _sendQuantity,
                1
            );
            if (underlying) {
                methodData = abi.encodeWithSignature(
                    'exchange_underlying(int128,int128,uint256,uint256)',
                    int128(i),
                    int128(j),
                    _sendQuantity,
                    1
                );
            }
        }

        return (curvePool, realSendToken == ETH_ADD_CURVE ? _sendQuantity : 0, methodData);
    }

    /**
     * Returns the address to approve source tokens to for trading. This is the TokenTaker address
     *
     * @return address             Address of the contract to approve tokens to
     */
    function _getSpender(address _swapTarget) internal pure override returns (address) {
        return _swapTarget;
    }

    /**
     * Return pre action calldata
     *
     * @param  _sendToken               Address of the asset to send
     * hparam  _receiveToken            Address of the asset to receive
     * @param  _sendQuantity            Amount of the asset to send
     *
     * @return address                   Target contract address
     * @return uint256                   Call value
     * @return bytes                     Trade calldata
     */
    function _getPreActionCallData(
        address _sendToken,
        address _receiveToken,
        uint256 _sendQuantity
    )
        internal
        view
        override
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        // Unwrap WETH to ETH
        (, address _realSendToken, ) = _getPoolAndTokens(_sendToken, _receiveToken);
        if (_realSendToken == ETH_ADD_CURVE) {
            bytes memory methodData = abi.encodeWithSignature('withdraw(uint256)', _sendQuantity);
            return (WETH, 0, methodData);
        }
        return (address(0), 0, bytes(''));
    }

    /**
     * Return post action calldata
     *
     * hparam  _sendToken               Address of the asset to send
     * @param  _receiveToken            Address of the asset to receive
     * @param  _sendQuantity            Amount of the asset to send
     *
     * @return address                   Target contract address
     * @return uint256                   Call value
     * @return bytes                     Trade calldata
     */
    function _getPostActionCallData(
        address _sendToken,
        address _receiveToken,
        uint256 _sendQuantity
    )
        internal
        view
        override
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        // Wrap ETH to WETH
        (, , address _realReceiveToken) = _getPoolAndTokens(_sendToken, _receiveToken);
        if (_realReceiveToken == ETH_ADD_CURVE) {
            bytes memory methodData = abi.encodeWithSignature('deposit()');
            return (WETH, _sendQuantity, methodData);
        }
        return (address(0), 0, bytes(''));
    }

    function _getPostActionToken(
        address /* _receiveToken */
    ) internal pure override returns (address) {
        return ETH_ADD_CURVE;
    }

    /* ============ Private Functions ============ */

    function _getPoolAndTokens(address _sendToken, address _receiveToken)
        private
        view
        returns (
            address,
            address,
            address
        )
    {
        address curvePool = curveMetaRegistry.findBestPoolForCoins(_sendToken, _receiveToken);
        if (curvePool == 0x828b154032950C8ff7CF8085D841723Db2696056) {
            curvePool = 0xDC24316b9AE028F1497c275EB9192a3Ea0f67022;
            _sendToken = _sendToken == WETH ? ETH_ADD_CURVE: _sendToken;
            _receiveToken = _receiveToken == WETH ? ETH_ADD_CURVE: _receiveToken;
        }
        if (_sendToken == WETH && curvePool == address(0)) {
            _sendToken = ETH_ADD_CURVE;
            curvePool = curveMetaRegistry.findBestPoolForCoins(ETH_ADD_CURVE, _receiveToken);
        }
        if (_receiveToken == WETH && curvePool == address(0)) {
            _receiveToken = ETH_ADD_CURVE;
            curvePool = curveMetaRegistry.findBestPoolForCoins(_sendToken, ETH_ADD_CURVE);
        }

        return (curvePool, _sendToken, _receiveToken);
    }
}
