// SPDX-License-Identifier: Apache-2.0

pragma solidity 0.7.6;
import {ERC20} from '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import {SignedSafeMath} from '@openzeppelin/contracts/math/SignedSafeMath.sol';
import {SafeCast} from '@openzeppelin/contracts/utils/SafeCast.sol';

import {IBabController} from '../interfaces/IBabController.sol';
import {IIntegration} from '../interfaces/IIntegration.sol';
import {IStrategy} from '../interfaces/IStrategy.sol';
import {IGarden} from '../interfaces/IGarden.sol';
import {IBaseIntegration} from '../interfaces/IBaseIntegration.sol';

import {LowGasSafeMath} from '../lib/LowGasSafeMath.sol';
import {PreciseUnitMath} from '../lib/PreciseUnitMath.sol';

/**
 * @title BaseIntegration
 * @author Babylon Finance
 *
 * Abstract class that houses common Integration-related state and functions.
 */
abstract contract BaseIntegration is IBaseIntegration {
    using SafeCast for int256;
    using LowGasSafeMath for uint256;
    using SignedSafeMath for int256;
    using PreciseUnitMath for uint256;

    /* ============ Modifiers ============ */

    modifier onlySystemContract() {
        require(controller.isSystemContract(msg.sender), 'Only system can call this');
        _;
    }

    /* ============ Constants ============ */

    address internal constant WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address internal constant DAI = 0x6B175474E89094C44Da98b954EedeAC495271d0F;
    address internal constant WBTC = 0x2260FAC5E5542a773Aa44fBCfeDf7C193bc2C599;
    address internal constant USDC = 0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48;
    address internal constant USDT = 0xdAC17F958D2ee523a2206206994597C13D831ec7;
    address internal constant ETH_ADD_CURVE = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
    address internal constant SNX = 0xC011a73ee8576Fb46F5E1c5751cA3B9Fe0af2a6F;
    address internal constant sETH = 0x5e74C9036fb86BD7eCdcb084a0673EFc32eA31cb;
    address internal constant sUSD = 0x57Ab1ec28D129707052df4dF418D58a2D46d5f51;
    address internal constant AAVE = 0x7Fc66500c84A76Ad7e9c93437bFc5Ac33E2DDaE9;

    /* ============ State Variables ============ */

    // Address of the controller
    IBabController public immutable controller;

    // Name of the integration
    string public override name;

    /* ============ Constructor ============ */

    /**
     * Creates the integration
     *
     * @param _name                   Name of the integration
     * @param _controller             Address of the controller
     */

    constructor(string memory _name, IBabController _controller) {
        require(address(_controller) != address(0), 'Controller must be defined');
        name = _name;
        controller = _controller;
    }

    /* ============ External Functions ============ */

    /* ============ Internal Functions ============ */

    function _getTokenOrETHBalance(address _strategy, address _token) internal view returns (uint256) {
        if (_token == address(0) || _token == ETH_ADD_CURVE) {
            return _strategy.balance;
        }
        return ERC20(_token).balanceOf(_strategy);
    }

    function _getDurationStrategy(address _strategy) internal view returns (uint256) {
        IStrategy strategy = IStrategy(_strategy);
        (, , , , uint256 executedAt, , ) = strategy.getStrategyState();
        return block.timestamp.sub(executedAt);
    }
}

contracts/strategies/operations/BuyOpera.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity 0.7.6;

import {ERC20} from '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import {Operation} from './Operation.sol';
import {IGarden} from '../../interfaces/IGarden.sol';
import {IStrategy} from '../../interfaces/IStrategy.sol';
import {PreciseUnitMath} from '../../lib/PreciseUnitMath.sol';
import {SafeDecimalMath} from '../../lib/SafeDecimalMath.sol';
import {BytesLib} from '../../lib/BytesLib.sol';
import {LowGasSafeMath as SafeMath} from '../../lib/LowGasSafeMath.sol';
import {ITradeIntegration} from '../../interfaces/ITradeIntegration.sol';

/**
 * @title BuyOperation
 * @author Babylon Finance
 *
 * Executes a buy operation
 */
contract BuyOperation is Operation {
    using SafeMath for uint256;
    using PreciseUnitMath for uint256;
    using SafeDecimalMath for uint256;
    using BytesLib for bytes;

    /* ============ Constructor ============ */

    /**
     * Creates the integration
     *
     * @param _name                   Name of the integration
     * @param _controller             Address of the controller
     */
    constructor(string memory _name, address _controller) Operation(_name, _controller) {}

    /**
     * Sets operation data for the buy operation
     *
     * @param _data                   Operation data
     */
    function validateOperation(
        bytes calldata _data,
        IGarden _garden,
        address, /* _integration */
        uint256 /* _index */
    ) external view override onlyStrategy {
        address asset = BytesLib.decodeOpDataAddress(_data);
        require(asset != address(0), 'Incorrect asset address');
        require(asset != _garden.reserveAsset(), 'Receive token must be different');
    }

    /**
     * Executes the buy operation
     * @param _asset              Asset to receive into this operation
     * @param _capital            Amount of asset received
     * param _assetStatus        Status of the asset amount
     * @param _data               OpData e.g. Address of the token to buy
     * param _garden             Garden of the strategy
     * param _integration        Address of the integration to execute
     */
    function executeOperation(
        address _asset,
        uint256 _capital,
        uint8, /* _assetStatus */
        bytes calldata _data,
        IGarden, /* _garden */
        address /* _integration */
    )
        external
        override
        onlyStrategy
        returns (
            address,
            uint256,
            uint8
        )
    {
        address token = BytesLib.decodeOpDataAddress(_data);
        // Replace old AXS with new AXS
        if (token == 0xF5D669627376EBd411E34b98F19C868c8ABA5ADA) {
            token = 0xBB0E17EF65F82Ab018d8EDd776e8DD940327B28b;
        }
        IStrategy(msg.sender).trade(_asset, _capital, token);
        return (token, ERC20(token).balanceOf(address(msg.sender)), 0); // liquid
    }

    /**
     * Exits the buy operation.
     * @param _percentage of capital to exit from the strategy
     */
    function exitOperation(
        address _asset,
        uint256, /* _remaining */
        uint8, /* _assetStatus */
        uint256 _percentage,
        bytes calldata, /*_data */
        IGarden, /*_garden */
        address /* _integration */
    )
        external
        view
        override
        onlyStrategy
        returns (
            address,
            uint256,
            uint8
        )
    {
        require(_percentage <= HUNDRED_PERCENT, 'Unwind Percentage <= 100%');
        uint256 balance = ERC20(_asset).balanceOf(address(msg.sender)).preciseMul(_percentage);
        return (_asset, balance, 0);
    }

    /**
     * Gets the NAV of the buy op in the reserve asset
     *
     * @param _data               OpData e.g. Asset bought
     * @param _garden             Garden the strategy belongs to
     * param _integration         Status of the asset amount
     * @return _nav               NAV of the strategy
     */
    function getNAV(
        bytes calldata _data,
        IGarden _garden,
        address /* _integration */
    ) external view override returns (uint256, bool) {
        address token = BytesLib.decodeOpDataAddress(_data); // 64 bytes (w/o signature prefix bytes4)
        if (!IStrategy(msg.sender).isStrategyActive()) {
            return (0, true);
        }
        // Replace old AXS with new AXS
        if (token == 0xF5D669627376EBd411E34b98F19C868c8ABA5ADA) {
            token = 0xBB0E17EF65F82Ab018d8EDd776e8DD940327B28b;
        }
        uint256 price = _getPriceNAV(_garden.reserveAsset(), token);
        uint256 NAV =
            SafeDecimalMath
                .normalizeAmountTokens(token, _garden.reserveAsset(), ERC20(token).balanceOf(msg.sender))
                .preciseDiv(price);
        require(NAV != 0, 'NAV has to be bigger 0');
        return (NAV, true);
    }
}

