// SPDX-License-Identifier: Apache-2.0

pragma solidity 0.7.6;
import {LowGasSafeMath} from '../lib/LowGasSafeMath.sol';
import {TimeLockedToken} from './TimeLockedToken.sol';
import {Address} from '@openzeppelin/contracts/utils/Address.sol';
import {ReentrancyGuard} from '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import {IBabController} from '../interfaces/IBabController.sol';

/**
 * @title BABL Token
 * @dev The BABLToken contract is ERC20 using 18 decimals as a standard
 * Is Ownable to transfer ownership to Governor Alpha for Decentralized Governance
 * It overrides the mint and maximum supply to control the timing and maximum cap allowed along the time.
 */

contract BABLToken is TimeLockedToken {
    using LowGasSafeMath for uint256;
    using Address for address;

    /* ============ Events ============ */

    /// @notice An event that emitted when a new mint ocurr
    event MintedNewTokens(address account, uint256 tokensminted);

    /// @notice An event thats emitted when maxSupplyAllowed changes
    event MaxSupplyChanged(uint256 previousMaxValue, uint256 newMaxValue);

    /// @notice An event that emitted when maxSupplyAllowedAfter changes
    event MaxSupplyAllowedAfterChanged(uint256 previousAllowedAfterValue, uint256 newAllowedAfterValue);

    /* ============ Modifiers ============ */

    /* ============ State Variables ============ */

    /// @dev EIP-20 token name for this token
    string private constant NAME = 'Babylon.Finance';

    /// @dev EIP-20 token symbol for this token
    string private constant SYMBOL = 'BABL';

    /// @dev Maximum number of tokens in circulation of 1 million for the first 8 years (using 18 decimals as ERC20 standard)
    uint256 public maxSupplyAllowed = 1_000_000e18; //

    /// @notice The timestamp after which a change on maxSupplyAllowed may occur
    uint256 public maxSupplyAllowedAfter;

    /// @notice Cap on the percentage of maxSupplyAllowed that can be increased per year after maxSupplyAllowedAfter
    uint8 public constant MAX_SUPPLY_CAP = 5;

    /// @notice Cap on the percentage of totalSupply that can be minted at each mint after the initial 1 Million BABL
    uint8 public constant MINT_CAP = 2;

    /// @notice The timestamp after which minting may occur after FIRST_EPOCH_MINT (8 years)
    uint256 public mintingAllowedAfter;

    /// @notice The timestamp of BABL Token deployment
    uint256 public BABLTokenDeploymentTimestamp;

    /// @dev First Epoch Mint where no more than 1 Million BABL can be minted (>= 8 Years)
    uint32 private constant FIRST_EPOCH_MINT = 365 days * 8;

    /// @dev Minimum time between mints after
    uint32 private constant MIN_TIME_BETWEEN_MINTS = 365 days;

    /* ============ Functions ============ */

    /* ============ Constructor ============ */

    /**
     * @notice Construct a new BABL token and gives ownership to sender
     */
    constructor(IBabController newController) TimeLockedToken(NAME, SYMBOL) {
        // Timestamp of contract deployment
        BABLTokenDeploymentTimestamp = block.timestamp;

        // Set-up the minimum time of 8 years to wait until the maxSupplyAllowed can be changed (it will also include a max cap)
        maxSupplyAllowedAfter = block.timestamp.add(FIRST_EPOCH_MINT);

        //Starting with a maxSupplyAllowed of 1 million for the first 8 years
        _mint(msg.sender, 1_000_000e18);

        //Set-up the minimum time of 8 years for additional mints
        mintingAllowedAfter = block.timestamp.add(FIRST_EPOCH_MINT);

        // Set the Babylon Controller
        controller = newController;
    }

    /* ============ External Functions ============ */

    /* ===========  Token related Gov Functions ====== */

    /**
     * PRIVILEGED GOVERNANCE FUNCTION. Allows to mint new tokens
     *
     * @notice Mint new BABL tokens. Initial 1 Million BABL. After 8 years new BABL could be minted by governance decision
     * @dev MINT_CAP The new maximum limit, limited by a 2% cap of totalSupply for each new mint and always limited by maxSupplyAllowed.
     * mintingAllowedAfter Defines the next time allowed for a new mint
     * @param _to The address of the destination account that will receive the new BABL tokens
     * @param _amount The number of tokens to be minted
     * @return Whether or not the mint succeeded
     */
    function mint(address _to, uint256 _amount) external onlyOwner returns (bool) {
        require(totalSupply().add(_amount) <= maxSupplyAllowed, 'BABLToken::mint: max supply exceeded');
        require(
            block.timestamp >= BABLTokenDeploymentTimestamp.add(FIRST_EPOCH_MINT),
            'BABLToken::mint: minting not allowed after the FIRST_EPOCH_MINT has passed >= 8 years'
        );
        require(_amount > 0, 'BABLToken::mint: mint should be higher than zero');
        require(
            block.timestamp >= mintingAllowedAfter,
            'BABLToken::mint: minting not allowed yet because mintingAllowedAfter'
        );
        require(_to != address(0), 'BABLToken::mint: cannot transfer to the zero address');
        require(_to != address(this), 'BABLToken::mint: cannot mint to the address of this contract');

        // set-up the new time where a new (the next) mint can be allowed
        mintingAllowedAfter = block.timestamp.add(MIN_TIME_BETWEEN_MINTS);

        // mint the amount
        uint96 amount = safe96(_amount, 'BABLToken::mint: amount exceeds 96 bits');

        // After FIRST_EPOCH_MINT (8 years) a MINT_CAP applies
        require(
            amount <= totalSupply().mul(MINT_CAP).div(100),
            'BABLToken::mint: exceeded mint cap of 2% of total supply'
        );
        _mint(_to, amount);

        emit MintedNewTokens(_to, amount);

        // move delegates to add voting power to the destination
        _moveDelegates(address(0), delegates[_to], amount);

        return true;
    }

    /**
     * PRIVILEGED GOVERNANCE FUNCTION. Allows governance to change maxSupplyAllowed
     *
     * @notice Set-up a greater maxSupplyAllowed value to allow more tokens to be minted
     * @param newMaxSupply The new maximum limit, limited by a maximum of 5% cap per year
     * @param newMaxSupplyAllowedAfter The new waiting period to change the maxSupplyAllowed limited for a minimum of 1 year
     * @return Whether or not the changeMaxSupply succeeded
     */
    function changeMaxSupply(uint256 newMaxSupply, uint256 newMaxSupplyAllowedAfter) external onlyOwner returns (bool) {
        require(
            block.timestamp >= BABLTokenDeploymentTimestamp.add(FIRST_EPOCH_MINT),
            'BABLToken::changeMaxSupply: a change on maxSupplyAllowed not allowed until 8 years after deployment'
        );
        require(
            block.timestamp >= maxSupplyAllowedAfter,
            'BABLToken::changeMaxSupply: a change on maxSupplyAllowed not allowed yet'
        );

        // update the amount
        require(
            newMaxSupply > maxSupplyAllowed,
            'BABLToken::changeMaxSupply: changeMaxSupply should be higher than previous value'
        );
        uint256 limitedNewSupply = maxSupplyAllowed.add(maxSupplyAllowed.mul(MAX_SUPPLY_CAP).div(100));
        require(newMaxSupply <= limitedNewSupply, 'BABLToken::changeMaxSupply: exceeded of allowed 5% cap');
        emit MaxSupplyChanged(maxSupplyAllowed, newMaxSupply);
        maxSupplyAllowed = safe96(newMaxSupply, 'BABLToken::changeMaxSupply: potential max amount exceeds 96 bits');

        // update the new waiting time until a new change could be done >= 1 year since this change
        uint256 futureTime = block.timestamp.add(365 days);
        require(
            newMaxSupplyAllowedAfter >= futureTime,
            'BABLToken::changeMaxSupply: the newMaxSupplyAllowedAfter should be at least 1 year in the future'
        );
        emit MaxSupplyAllowedAfterChanged(maxSupplyAllowedAfter, newMaxSupplyAllowedAfter);
        maxSupplyAllowedAfter = safe96(
            newMaxSupplyAllowedAfter,
            'BABLToken::changeMaxSupply: new newMaxSupplyAllowedAfter exceeds 96 bits'
        );

        return true;
    }

    /**
     * PUBLIC FUNCTION. Get the value of maxSupplyAllowed
     *
     * @return Returns the value of maxSupplyAllowed at the time
     */
    function maxSupply() external view returns (uint96, uint256) {
        uint96 safeMaxSupply =
            safe96(maxSupplyAllowed, 'BABLToken::maxSupplyAllowed: maxSupplyAllowed exceeds 96 bits'); // Overflow check
        return (safeMaxSupply, maxSupplyAllowedAfter);
    }
}

contracts/Treasury.sol.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity 0.7.6;

import {Address} from '@openzeppelin/contracts/utils/Address.sol';
import {Ownable} from '@openzeppelin/contracts/access/Ownable.sol';
import {IERC20} from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import {SafeERC20} from '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import {ReentrancyGuard} from '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import {IBabController} from './interfaces/IBabController.sol';

/**
 * @title Treasury
 * @author Babylon Finance
 *
 * Contract that will receive the fees earned by the protocol.
 * Governance will be able to send funds from the treasury.
 */
contract Treasury is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using Address for address;
    /* ============ Events ============ */

    event TreasuryFundsSent(address _asset, uint256 _amount, address _to);

    /* ============ State Variables ============ */

    /* ============ Constructor ============ */

    /* ============ External Functions ============ */

    /**
     * GOVERNANCE FUNCTION: Send an asset amount to an address
     *
     * @param _asset            Address of the asset to send
     * @param _amount           Amount to send of the asset
     * @param _to               Address to send the assets to
     */
    function sendTreasuryFunds(
        address _asset,
        uint256 _amount,
        address _to
    ) external onlyOwner nonReentrant {
        require(_asset != address(0), 'Asset must exist');
        require(_to != address(0), 'Target address must exist');

        IERC20(_asset).safeTransfer(_to, _amount);

        emit TreasuryFundsSent(_asset, _amount, _to);
    }

    /**
     * GOVERNANCE FUNCTION: Send an ETH amount to an address
     *
     * @param _amount           Amount to send of the asset
     * @param _to               Address to send the assets to
     */
    function sendTreasuryETH(uint256 _amount, address payable _to) external onlyOwner nonReentrant {
        require(_to != address(0), 'Target address must exist');
        require(address(this).balance >= _amount, 'Not enough funds in treasury');

        Address.sendValue(_to, _amount);

        emit TreasuryFundsSent(address(0), _amount, _to);
    }

    // Can receive ETH
    // solhint-disable-next-line
    receive() external payable {}
}

contracts/integrations/trade/TradeIntegr.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity 0.7.6;

import {SafeCast} from '@openzeppelin/contracts/utils/SafeCast.sol';
import {ERC20} from '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import {ReentrancyGuard} from '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol';
import {IStrategy} from '../../interfaces/IStrategy.sol';
import {ITradeIntegration} from '../../interfaces/ITradeIntegration.sol';
import {IGarden} from '../../interfaces/IGarden.sol';
import {IBabController} from '../../interfaces/IBabController.sol';
import {BaseIntegration} from '../BaseIntegration.sol';
import {LowGasSafeMath} from '../../lib/LowGasSafeMath.sol';
import {PreciseUnitMath} from '../../lib/PreciseUnitMath.sol';

/**
 * @title TradeIntegration
 * @author Babylon Finance Protocol
 *
 * Base class for integration with trading protocols
 */
abstract contract TradeIntegration is BaseIntegration, ReentrancyGuard, ITradeIntegration {
    using LowGasSafeMath for uint256;
    using SafeCast for uint256;
    using PreciseUnitMath for uint256;

    /* ============ Struct ============ */

    struct TradeInfo {
        IGarden garden; // Garden
        IStrategy strategy; // Strategy
        string exchangeName; // Which exchange to use
        address sendToken; // Address of token being sold
        address receiveToken; // Address of token being bought
        uint256 gardenTotalSupply; // Total supply of Garden in Precise Units (10^18)
        uint256 totalSendQuantity; // Total quantity of sold tokens
        uint256 totalMinReceiveQuantity; // Total minimum quantity of token to receive back
        uint256 preTradeSendTokenBalance; // Total initial balance of token being sold
        uint256 preTradeReceiveTokenBalance; // Total initial balance of token being bought
    }

    /* ============ Events ============ */

    /* ============ Constants ============ */

    uint24 internal constant FEE_LOW = 500;
    uint24 internal constant FEE_MEDIUM = 3000;
    uint24 internal constant FEE_HIGH = 10000;
    IUniswapV3Factory internal constant uniswapFactory = IUniswapV3Factory(0x1F98431c8aD98523631AE4a59f267346ea31F984);

    /* ============ Constructor ============ */

    /**
     * Creates the integration
     *
     * @param _name                   Name of the integration
     * @param _controller             Address of the controller
     */
    constructor(string memory _name, IBabController _controller) BaseIntegration(_name, _controller) {}

    /* ============ External Functions ============ */

    /**
     * Executes a trade on a supported DEX.
     * @dev
     *
     * @param _strategy             Address of the strategy
     * @param _sendToken            Address of the token to be sent to the exchange
     * @param _sendQuantity         Units of reserve asset token sent to the exchange
     * @param _receiveToken         Address of the token that will be received from the exchange
     * @param _minReceiveQuantity   Min units of wanted token to be received from the exchange
     */
    function trade(
        address _strategy,
        address _sendToken,
        uint256 _sendQuantity,
        address _receiveToken,
        uint256 _minReceiveQuantity,
        address _hopToken
    ) public override nonReentrant onlySystemContract {
        _trade(_strategy, _sendToken, _sendQuantity, _receiveToken, _minReceiveQuantity, _hopToken);
    }

    /**
     * Executes a trade on a supported DEX.
     * @dev
     *
     * @param _strategy             Address of the strategy
     * @param _sendToken            Address of the token to be sent to the exchange
     * @param _sendQuantity         Units of reserve asset token sent to the exchange
     * @param _receiveToken         Address of the token that will be received from the exchange
     * @param _minReceiveQuantity   Min units of wanted token to be received from the exchange
     */
    function trade(
        address _strategy,
        address _sendToken,
        uint256 _sendQuantity,
        address _receiveToken,
        uint256 _minReceiveQuantity
    ) external override nonReentrant onlySystemContract {
        _trade(_strategy, _sendToken, _sendQuantity, _receiveToken, _minReceiveQuantity, address(0));
    }

    /* ============ Internal Functions ============ */

    /**
     * Executes a trade on a supported DEX.
     * @dev
     *
     * @param _strategy             Address of the strategy
     * @param _sendToken            Address of the token to be sent to the exchange
     * @param _sendQuantity         Units of reserve asset token sent to the exchange
     * @param _receiveToken         Address of the token that will be received from the exchange
     * @param _minReceiveQuantity   Min units of wanted token to be received from the exchange
     */
    function _trade(
        address _strategy,
        address _sendToken,
        uint256 _sendQuantity,
        address _receiveToken,
        uint256 _minReceiveQuantity,
        address _hopToken
    ) internal {
        TradeInfo memory tradeInfo =
            _createTradeInfo(_strategy, name, _sendToken, _receiveToken, _sendQuantity, _minReceiveQuantity);
        _validatePreTradeData(tradeInfo, _sendQuantity);
        // Pre actions
        (address targetAddressP, uint256 callValueP, bytes memory methodDataP) =
            _getPreActionCallData(_sendToken, _receiveToken, _sendQuantity);
        if (targetAddressP != address(0)) {
            // Invoke protocol specific call
            if (_getPreApprovalSpender(targetAddressP) != address(0)) {
                tradeInfo.strategy.invokeApprove(
                    _getPreApprovalSpender(targetAddressP),
                    tradeInfo.sendToken,
                    tradeInfo.totalSendQuantity
                );
            }
            tradeInfo.strategy.invokeFromIntegration(targetAddressP, callValueP, methodDataP);
        }
        // Get spender address from exchange adapter and invoke approve for exact amount on sendToken
        (address targetExchange, uint256 callValue, bytes memory methodData) =
            _hopToken != address(0)
                ? _getTradeCallData(
                    _strategy,
                    tradeInfo.sendToken,
                    tradeInfo.totalSendQuantity,
                    tradeInfo.receiveToken,
                    _hopToken
                )
                : _getTradeCallData(
                    _strategy,
                    tradeInfo.sendToken,
                    tradeInfo.totalSendQuantity,
                    tradeInfo.receiveToken
                );
        if (targetExchange != address(0)) {
            // Get spender address from exchange adapter and invoke approve for exact amount on sendToken
            tradeInfo.strategy.invokeApprove(
                _getSpender(targetExchange),
                tradeInfo.sendToken,
                tradeInfo.totalSendQuantity
            );
            tradeInfo.strategy.invokeFromIntegration(targetExchange, callValue, methodData);
        }
        // Post actions
        uint256 receiveTokenAmount = _getTokenOrETHBalance(address(_strategy), _getPostActionToken(_receiveToken));
        (targetAddressP, callValueP, methodDataP) = _getPostActionCallData(
            _sendToken,
            _receiveToken,
            receiveTokenAmount
        );
        if (targetAddressP != address(0)) {
            // Invoke protocol specific call
            if (_getPostApprovalSpender(targetAddressP) != address(0)) {
                tradeInfo.strategy.invokeApprove(
                    _getPostApprovalSpender(targetAddressP),
                    _getPostActionToken(_receiveToken),
                    receiveTokenAmount
                );
            }
            // Invoke protocol specific call
            tradeInfo.strategy.invokeFromIntegration(targetAddressP, callValueP, methodDataP);
        }

        _validatePostTrade(tradeInfo);
    }

    /**
     * Create and return TradeInfo struct
     *
     * @param _strategy             Address of the strategy
     * @param _exchangeName         Human readable name of the exchange in the integrations registry
     * @param _sendToken            Address of the token to be sent to the exchange
     * @param _receiveToken         Address of the token that will be received from the exchange
     * @param _sendQuantity         Units of token in SetToken sent to the exchange
     * @param _minReceiveQuantity   Min units of token in SetToken to be received from the exchange
     *
     * return TradeInfo             Struct containing data for trade
     */
    function _createTradeInfo(
        address _strategy,
        string memory _exchangeName,
        address _sendToken,
        address _receiveToken,
        uint256 _sendQuantity,
        uint256 _minReceiveQuantity
    ) internal view returns (TradeInfo memory) {
        TradeInfo memory tradeInfo;

        tradeInfo.strategy = IStrategy(_strategy);
        tradeInfo.garden = tradeInfo.strategy.garden();

        tradeInfo.exchangeName = _exchangeName;

        tradeInfo.sendToken = _sendToken;
        tradeInfo.receiveToken = _receiveToken;

        tradeInfo.gardenTotalSupply = ERC20(address(tradeInfo.strategy.garden())).totalSupply();

        tradeInfo.totalSendQuantity = _sendQuantity;

        tradeInfo.totalMinReceiveQuantity = _minReceiveQuantity;
        tradeInfo.preTradeSendTokenBalance = ERC20(_sendToken).balanceOf(_strategy);
        tradeInfo.preTradeReceiveTokenBalance = ERC20(_receiveToken).balanceOf(_strategy);
        return tradeInfo;
    }

    /**
     * Validate pre trade data. Check exchange is valid, token quantity is valid.
     *
     * @param _tradeInfo            Struct containing trade information used in internal functions
     * @param _sendQuantity         Amount of tokens sent
     */
    function _validatePreTradeData(TradeInfo memory _tradeInfo, uint256 _sendQuantity) internal view {
        require(_tradeInfo.totalSendQuantity > 0, 'Token to sell must be nonzero');
        require(
            ERC20(_tradeInfo.sendToken).balanceOf(address(_tradeInfo.strategy)) >= _sendQuantity,
            'Strategy needs to have enough liquid tokens'
        );
    }

    /**
     * Validate post trade data.
     *
     * @param _tradeInfo                Struct containing trade information used in internal functions
     * @return uint256                  Total quantity of receive token that was exchanged
     */
    function _validatePostTrade(TradeInfo memory _tradeInfo) internal view returns (uint256) {
        uint256 exchangedQuantity =
            ERC20(_tradeInfo.receiveToken).balanceOf(address(_tradeInfo.strategy)).sub(
                _tradeInfo.preTradeReceiveTokenBalance
            );
        uint256 sendTokenBalance = ERC20(_tradeInfo.sendToken).balanceOf(address(_tradeInfo.strategy));
        uint256 realUsed = _tradeInfo.preTradeSendTokenBalance.sub(sendTokenBalance);
        // Uses at least 90% of the send token (disallow partial liquidity trades)
        require(realUsed >= _tradeInfo.totalSendQuantity.preciseMul(9e17), 'Partial trade not allowed');
        require(exchangedQuantity >= _tradeInfo.totalMinReceiveQuantity, 'Slippage greater than allowed');
        return exchangedQuantity;
    }

    /**
     * Return exchange calldata which is already generated from the exchange API
     *
     * hparam _strategy             Address of the strategy
     * hparam _sendToken            Address of the token to be sent to the exchange
     * hparam _sendQuantity         Units of reserve asset token sent to the exchange
     * hparam _receiveToken         Address of the token that will be received from the exchange
     *
     * @return address                   Target contract address
     * @return uint256                   Call value
     * @return bytes                     Trade calldata
     */
    function _getTradeCallData(
        address, /* _strategy */
        address, /* _sendToken */
        uint256, /* _sendQuantity */
        address, /* _receiveToken */
        address /* _hopToken */
    )
        internal
        view
        virtual
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        return (address(0), 0, bytes(''));
    }

    /**
     * Return exchange calldata which is already generated from the exchange API
     *
     * hparam _strategy             Address of the strategy
     * hparam _sendToken            Address of the token to be sent to the exchange
     * hparam _sendQuantity         Units of reserve asset token sent to the exchange
     * hparam _receiveToken         Address of the token that will be received from the exchange
     *
     * @return address                   Target contract address
     * @return uint256                   Call value
     * @return bytes                     Trade calldata
     */
    function _getTradeCallData(
        address, /* _strategy */
        address, /* _sendToken */
        uint256, /*_sendQuantity */
        address /* _receiveToken */
    )
        internal
        view
        virtual
        returns (
            address,
            uint256,
            bytes memory
        );

    /**
     * Return pre action calldata
     *
     * hparam  _sendToken               Address of the asset to send
     * hparam  _receiveToken            Address of the asset to receive
     * hparam  _sendQuantity            Amount of the asset to send
     *
     * @return address                   Target contract address
     * @return uint256                   Call value
     * @return bytes                     Trade calldata
     */
    function _getPreActionCallData(
        address, /* _sendToken */
        address, /* _receiveToken */
        uint256 /* _sendQuantity */
    )
        internal
        view
        virtual
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        return (address(0), 0, bytes(''));
    }

    /**
     * Return pre action calldata
     *
     * hparam  _sendToken               Address of the asset to send
     * hparam  _receiveToken            Address of the asset to receive
     * hparam  _sendQuantity            Amount of the asset to send
     *
     * @return address                   Target contract address
     * @return uint256                   Call value
     * @return bytes                     Trade calldata
     */
    function _getPostActionCallData(
        address, /* _sendToken */
        address, /* _receiveToken */
        uint256 /* _sendQuantity */
    )
        internal
        view
        virtual
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        return (address(0), 0, bytes(''));
    }

    /**
     * Returns the address to approve source tokens to for trading. This is the TokenTaker address
     *
     * @param _swapTarget      Address of the contracts that executes the swap
     * @return address         Address of the contract to approve tokens to
     */
    function _getSpender(address _swapTarget) internal view virtual returns (address);

    /**
     * Returns the address to approve the pre action. This is the TokenTaker address
     *
     * hparam _swapTarget      Address of the contracts that executes the swap
     * @return address         Address of the contract to approve tokens to
     */
    function _getPreApprovalSpender(
        address /* _swapTarget */
    ) internal view virtual returns (address) {
        return address(0);
    }

    /**
     * Returns the address to approve the post action. This is the TokenTaker address
     *
     * hparam _swapTarget      Address of the contracts that executes the swap
     * @return address         Address of the contract to approve tokens to
     */
    function _getPostApprovalSpender(
        address /* _swapTarget */
    ) internal view virtual returns (address) {
        return address(0);
    }

    function _getPostActionToken(address _receiveToken) internal view virtual returns (address) {
        return _receiveToken;
    }
}

contracts/integrations/trade/UniswapV3Tr.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import {IBabController} from '../../interfaces/IBabController.sol';
import {ERC20} from '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import {IPriceOracle} from '../../interfaces/IPriceOracle.sol';
import {ISwapRouter} from '../../interfaces/external/uniswap-v3/ISwapRouter.sol';
import '@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol';
import {LowGasSafeMath as SafeMath} from '../../lib/LowGasSafeMath.sol';
import {TradeIntegration} from './TradeIntegration.sol';
import {PreciseUnitMath} from '../../lib/PreciseUnitMath.sol';
import {SafeDecimalMath} from '../../lib/SafeDecimalMath.sol';

/**
 * @title UniswapV3TradeIntegration
 * @author Babylon Finance Protocol
 *
 * UniswapV3 trade integration
 */
contract UniswapV3TradeIntegration is TradeIntegration {
    using SafeMath for uint256;
    using PreciseUnitMath for uint256;
    using SafeDecimalMath for uint256;

    /* ============ Modifiers ============ */

    /* ============ State Variables ============ */

    /* ============ Constants ============ */
    // Address of Uniswap V3 SwapRouter contract
    address private constant swapRouter = 0xE592427A0AEce92De3Edee1F18E0157C05861564;

    /* ============ Constructor ============ */

    /**
     * Creates the integration
     *
     * @param _controller                   Address of the controller
     */
    constructor(IBabController _controller) TradeIntegration('univ3_3', _controller) {}

    /* ============ Internal Functions ============ */

    /**
     * Executes the trade through UniswapV3.
     *
     * @param _strategy             Address of the strategy
     * @param _sendToken            Address of the token to be sent to the exchange
     * @param _sendQuantity         Units of reserve asset token sent to the exchange
     * @param _receiveToken         Address of the token that will be received from the exchange
     * @param _hopToken             Address of the routing token for multi-hop, i.e., sendToken->hopToken->receiveToken
     */
    function _getTradeCallData(
        address _strategy,
        address _sendToken,
        uint256 _sendQuantity,
        address _receiveToken,
        address _hopToken
    )
        internal
        view
        override
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        bytes memory path;
        if (_hopToken == address(0) || _sendToken == _hopToken || _receiveToken == _hopToken) {
            (, uint24 fee) = _getUniswapPoolWithHighestLiquidity(_sendToken, _receiveToken);
            path = abi.encodePacked(_sendToken, fee, _receiveToken);
        } else {
            (, uint24 fee0) = _getUniswapPoolWithHighestLiquidity(_sendToken, _hopToken);
            (, uint24 fee1) = _getUniswapPoolWithHighestLiquidity(_receiveToken, _hopToken);
            path = abi.encodePacked(_sendToken, fee0, _hopToken, fee1, _receiveToken);
        }
        ISwapRouter.ExactInputParams memory params =
            ISwapRouter.ExactInputParams(
                path,
                _strategy,
                block.timestamp,
                _sendQuantity,
                1 // we check for amountOutMinimum in the post trade check
            );

        bytes memory callData = abi.encodeWithSignature('exactInput((bytes,address,uint256,uint256,uint256))', params);
        return (swapRouter, 0, callData);
    }

    /**
     * Executes the trade through UniswapV3.
     *
     * @param _strategy             Address of the strategy
     * @param _sendToken            Address of the token to be sent to the exchange
     * @param _sendQuantity         Units of reserve asset token sent to the exchange
     * @param _receiveToken         Address of the token that will be received from the exchange
     */
    function _getTradeCallData(
        address _strategy,
        address _sendToken,
        uint256 _sendQuantity,
        address _receiveToken
    )
        internal
        view
        override
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        return _getTradeCallData(_strategy, _sendToken, _sendQuantity, _receiveToken, WETH);
    }

    /**
     * Returns the address to approve source tokens to for trading. This is the TokenTaker address
     *
     * @return address             Address of the contract to approve tokens to
     */
    function _getSpender(
        address /* _swapTarget */
    ) internal pure override returns (address) {
        return address(swapRouter);
    }

    /* ============ Private Functions ============ */

    function _getUniswapPoolWithHighestLiquidity(address sendToken, address receiveToken)
        private
        view
        returns (IUniswapV3Pool pool, uint24 fee)
    {
        IUniswapV3Pool poolLow = IUniswapV3Pool(uniswapFactory.getPool(sendToken, receiveToken, FEE_LOW));
        IUniswapV3Pool poolMedium = IUniswapV3Pool(uniswapFactory.getPool(sendToken, receiveToken, FEE_MEDIUM));
        IUniswapV3Pool poolHigh = IUniswapV3Pool(uniswapFactory.getPool(sendToken, receiveToken, FEE_HIGH));

        uint128 liquidityLow = address(poolLow) != address(0) ? poolLow.liquidity() : 0;
        uint128 liquidityMedium = address(poolMedium) != address(0) ? poolMedium.liquidity() : 0;
        uint128 liquidityHigh = address(poolHigh) != address(0) ? poolHigh.liquidity() : 0;
        if (liquidityLow > liquidityMedium && liquidityLow >= liquidityHigh) {
            return (poolLow, FEE_LOW);
        }
        if (liquidityMedium > liquidityLow && liquidityMedium >= liquidityHigh) {
            return (poolMedium, FEE_MEDIUM);
        }
        return (poolHigh, FEE_HIGH);
    }

    function _getReserveAsWeth(address _token, address _reserveAsset) internal pure returns (address) {
        return _reserveAsset == _token ? WETH : _token;
    }
}

