contracts/integrations/pool/CurvePoolInt.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity 0.7.6;

import {IERC20} from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import {IBabController} from '../../interfaces/IBabController.sol';
import {ICurveMetaRegistry} from '../../interfaces/ICurveMetaRegistry.sol';
import {PoolIntegration} from './PoolIntegration.sol';
import {PreciseUnitMath} from '../../lib/PreciseUnitMath.sol';
import {LowGasSafeMath} from '../../lib/LowGasSafeMath.sol';
import {BytesLib} from '../../lib/BytesLib.sol';
import {ControllerLib} from '../../lib/ControllerLib.sol';

/**
 * @title CurvePoolIntegration
 * @author Babylon Finance Protocol
 *
 * Curve liquidity providing integration
 */
contract CurvePoolIntegration is PoolIntegration {
    using LowGasSafeMath for uint256;
    using PreciseUnitMath for uint256;
    using BytesLib for uint256;
    using ControllerLib for IBabController;

    /* ============ Constant ============ */
    address private constant TRICRYPTO2 = 0xD51a44d3FaE010294C616388b506AcdA1bfAAE46; // Pool only takes ETH
    address private constant cvxCRVPool = 0x9D0464996170c6B9e75eED71c68B99dDEDf279e8; // Pool only takes CRV for us
    address private constant palstkaave = 0x48536EC5233297C367fd0b6979B75d9270bB6B15; // Pool only takes CRV for us
    address private constant CRV = 0xD533a949740bb3306d119CC777fa900bA034cd52; // crv
    address private constant CVX = 0x4e3FBD56CD56c3e72c1403e103b45Db9da5B9D2B; // cvx
    ICurveMetaRegistry private immutable curveMetaRegistry;

    /* ============ State Variables ============ */

    // Registry of first party pools

    // Mapping of pools to deposit contract
    mapping(address => address) private poolToDeposit;

    // Whether to deposit using the underlying coins
    mapping(address => bool) private usesUnderlying;

    // Whether it supports the underlying param in add liquidity and remove liquidity
    mapping(address => bool) private supportsUnderlyingParam;

    /* ============ Constructor ============ */

    /**
     * Creates the integration
     *
     * @param _controller                   Address of the controller
     */
    constructor(IBabController _controller, ICurveMetaRegistry _curveMetaRegistry)
        PoolIntegration('curve_pool', _controller)
    {
        require(address(_controller) != address(0), 'invalid address');
        require(address(_curveMetaRegistry) != address(0), 'invalid address');

        usesUnderlying[0xDeBF20617708857ebe4F679508E7b7863a8A8EeE] = true; // aave
        usesUnderlying[0xA2B47E3D5c44877cca798226B7B8118F9BFb7A56] = true; // compound
        usesUnderlying[0x52EA46506B9CC5Ef470C5bf89f17Dc28bB35D85C] = true; // usdt
        usesUnderlying[0x06364f10B501e868329afBc005b3492902d6C763] = true; // PAX
        usesUnderlying[0x2dded6Da1BF5DBdF597C45fcFaa3194e53EcfeAF] = true; // ironbank
        usesUnderlying[0x79a8C46DeA5aDa233ABaFFD40F3A0A2B1e5A4F27] = true; // busd
        usesUnderlying[0x45F783CCE6B7FF23B2ab2D70e416cdb7D6055f51] = true; // y
        usesUnderlying[0xA5407eAE9Ba41422680e2e00537571bcC53efBfD] = true; // susd
        usesUnderlying[0x8925D9d9B4569D737a48499DeF3f67BaA5a144b9] = true; // yv2
        usesUnderlying[0xEB16Ae0052ed37f479f7fe63849198Df1765a733] = true; // saave

        poolToDeposit[0xA2B47E3D5c44877cca798226B7B8118F9BFb7A56] = 0xeB21209ae4C2c9FF2a86ACA31E123764A3B6Bc06; // compound
        poolToDeposit[0x52EA46506B9CC5Ef470C5bf89f17Dc28bB35D85C] = 0xac795D2c97e60DF6a99ff1c814727302fD747a80; // usdt
        poolToDeposit[0x06364f10B501e868329afBc005b3492902d6C763] = 0xA50cCc70b6a011CffDdf45057E39679379187287; // pax
        poolToDeposit[0x79a8C46DeA5aDa233ABaFFD40F3A0A2B1e5A4F27] = 0xb6c057591E073249F2D9D88Ba59a46CFC9B59EdB; // busd
        poolToDeposit[0x45F783CCE6B7FF23B2ab2D70e416cdb7D6055f51] = 0xbBC81d23Ea2c3ec7e56D39296F0cbB648873a5d3; // y
        poolToDeposit[0xA5407eAE9Ba41422680e2e00537571bcC53efBfD] = 0xFCBa3E75865d2d561BE8D220616520c171F12851; // susd

        supportsUnderlyingParam[0xDeBF20617708857ebe4F679508E7b7863a8A8EeE] = true; // aave
        supportsUnderlyingParam[0x2dded6Da1BF5DBdF597C45fcFaa3194e53EcfeAF] = true; // ironbank
        supportsUnderlyingParam[0x8925D9d9B4569D737a48499DeF3f67BaA5a144b9] = true; // yv2
        supportsUnderlyingParam[0xEB16Ae0052ed37f479f7fe63849198Df1765a733] = true; // saave

        curveMetaRegistry = _curveMetaRegistry;
    }

    /* ============ External Functions ============ */

    function getPoolTokens(bytes calldata _pool, bool forNAV) public view override returns (address[] memory) {
        address poolAddress = BytesLib.decodeOpDataAddress(_pool);
        uint256 ncoins = curveMetaRegistry.getNCoins(poolAddress);
        address[] memory result = new address[](ncoins);
        address[8] memory coins =
            curveMetaRegistry.getCoinAddresses(poolAddress, usesUnderlying[poolAddress] && !forNAV);
        for (uint8 i = 0; i < ncoins; i++) {
            result[i] = coins[i];
        }
        return result;
    }

    function getPoolWeights(bytes calldata _pool) external view override returns (uint256[] memory) {
        address poolAddress = BytesLib.decodeOpDataAddress(_pool);
        address[] memory poolTokens = getPoolTokens(_pool, false);
        uint256[] memory result = new uint256[](curveMetaRegistry.getNCoins(poolAddress));
        if (poolAddress == TRICRYPTO2) {
            result[0] = 0;
            result[1] = 0;
            result[2] = uint256(1e18);
            return result;
        }
        // cvxCRV
        if (poolAddress == cvxCRVPool) {
            result[0] = uint256(1e18);
            result[1] = 0;
            return result;
        }
        // If it's a meta pool, deposit and withdraw from the stable one
        if (curveMetaRegistry.isMeta(poolAddress)) {
            result[0] = uint256(1e18);
        } else {
            for (uint8 i = 0; i < poolTokens.length; i++) {
                result[i] = uint256(1e18).div(poolTokens.length);
            }
        }
        return result;
    }

    function getPoolTokensOut(
        bytes calldata, /* _pool */
        address, /* _poolToken */
        uint256 /* _maxAmountsIn */
    ) external pure override returns (uint256) {
        // return 1 since _poolTokensOut are not used
        return 1;
    }

    function getPoolMinAmountsOut(
        bytes calldata _pool,
        uint256 /* _liquidity */
    ) external view override returns (uint256[] memory _minAmountsOut) {
        address poolAddress = BytesLib.decodeOpDataAddress(_pool);
        uint256[] memory result = new uint256[](curveMetaRegistry.getNCoins(poolAddress));
        return result;
    }

    /* ============ Internal Functions ============ */

    function _isPool(bytes memory _pool) internal view override returns (bool) {
        address poolAddress = BytesLib.decodeOpDataAddressAssembly(_pool, 12);
        return curveMetaRegistry.isPool(poolAddress);
    }

    function _getSpender(
        bytes calldata _pool,
        uint8 /* _opType */
    ) internal view override returns (address) {
        address poolAddress = BytesLib.decodeOpDataAddress(_pool);
        if (poolToDeposit[poolAddress] != address(0)) {
            poolAddress = poolToDeposit[poolAddress];
        }
        return poolAddress;
    }

    function _totalSupply(
        address /* _pool */
    ) internal pure override returns (uint256) {
        return uint256(1e18);
    }

    /**
     * Return join pool calldata which is already generated from the pool API
     *
     * hparam  _strategy                 Address of the strategy
     * @param  _pool                     OpData e.g. Address of the pool
     * @param  _poolTokensOut            Amount of pool tokens to send
     * hparam  _tokensIn                 Addresses of tokens to send to the pool
     * @param  _maxAmountsIn             Amounts of tokens to send to the pool
     *
     * @return address                   Target contract address
     * @return uint256                   Call value
     * @return bytes                     Trade calldata
     */
    function _getJoinPoolCalldata(
        address, /* _strategy */
        bytes calldata _pool,
        uint256 _poolTokensOut,
        address[] calldata _tokensIn,
        uint256[] calldata _maxAmountsIn
    )
        internal
        view
        override
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        address poolAddress = BytesLib.decodeOpDataAddress(_pool);
        uint256 poolCoins = curveMetaRegistry.getNCoins(poolAddress); //_decodeOpDataAsUint8(_pool, 0);
        // Encode method data for Garden to invoke
        bytes memory methodData = _getAddLiquidityMethodData(poolAddress, poolCoins, _maxAmountsIn, _poolTokensOut);

        uint256 value = 0;
        // If any is eth, set as value
        for (uint256 i = 0; i < poolCoins; i++) {
            if (_tokensIn[i] == address(0) || _tokensIn[i] == ETH_ADD_CURVE) {
                value = _maxAmountsIn[i];
            }
        }
        // If we need a deposit contract to deposit underlying, switch
        if (poolToDeposit[poolAddress] != address(0)) {
            poolAddress = poolToDeposit[poolAddress];
        }
        return (poolAddress, value, methodData);
    }

    /**
     * Return exit pool calldata which is already generated from the pool API
     *
     * hparam  _strategy                 Address of the strategy
     * @param  _pool                     OpData e.g. Address of the pool
     * @param  _poolTokensIn             Amount of pool tokens to receive
     * hparam  _tokensOut                Addresses of tokens to receive
     * @param  _minAmountsOut            Amounts of pool tokens to receive
     *
     * @return address                   Target contract address
     * @return uint256                   Call value
     * @return bytes                     Trade calldata
     */
    function _getExitPoolCalldata(
        address, /* _strategy */
        bytes memory _pool,
        uint256 _poolTokensIn,
        address[] calldata, /* _tokensOut */
        uint256[] calldata _minAmountsOut
    )
        internal
        view
        override
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        address poolAddress = BytesLib.decodeOpDataAddressAssembly(_pool, 12);
        uint256 poolCoins = curveMetaRegistry.getNCoins(poolAddress); //_decodeOpDataAsUint8(_pool, 0);

        require(_poolTokensIn > 0, '_poolTokensIn has to not 0');
        require(_minAmountsOut.length > 1, 'Has to provide _minAmountsOut');
        // Encode method data for Garden to invoke
        bytes memory methodData = _getRemoveLiquidityMethodData(poolAddress, poolCoins, _minAmountsOut, _poolTokensIn);
        if (poolToDeposit[poolAddress] != address(0)) {
            poolAddress = poolToDeposit[poolAddress];
        }
        return (poolAddress, 0, methodData);
    }

    function _getAddLiquidityMethodData(
        address _poolAddress,
        uint256 ncoins,
        uint256[] calldata _maxAmountsIn,
        uint256 minMintAmount
    ) private view returns (bytes memory) {
        if (ncoins == 2) {
            if (supportsUnderlyingParam[_poolAddress]) {
                return
                    abi.encodeWithSignature(
                        'add_liquidity(uint256[2],uint256,bool)',
                        _maxAmountsIn[0],
                        _maxAmountsIn[1],
                        minMintAmount,
                        true
                    );
            } else {
                return
                    abi.encodeWithSignature(
                        'add_liquidity(uint256[2],uint256)',
                        _maxAmountsIn[0],
                        _maxAmountsIn[1],
                        minMintAmount
                    );
            }
        }
        if (ncoins == 3) {
            if (supportsUnderlyingParam[_poolAddress]) {
                return
                    abi.encodeWithSignature(
                        'add_liquidity(uint256[3],uint256,bool)',
                        _maxAmountsIn[0],
                        _maxAmountsIn[1],
                        _maxAmountsIn[2],
                        minMintAmount,
                        true
                    );
            } else {
                return
                    abi.encodeWithSignature(
                        'add_liquidity(uint256[3],uint256)',
                        _maxAmountsIn[0],
                        _maxAmountsIn[1],
                        _maxAmountsIn[2],
                        minMintAmount
                    );
            }
        }
        if (ncoins == 4) {
            if (supportsUnderlyingParam[_poolAddress]) {
                return
                    abi.encodeWithSignature(
                        'add_liquidity(uint256[4],uint256,bool)',
                        _maxAmountsIn[0],
                        _maxAmountsIn[1],
                        _maxAmountsIn[2],
                        _maxAmountsIn[3],
                        minMintAmount,
                        true
                    );
            } else {
                return
                    abi.encodeWithSignature(
                        'add_liquidity(uint256[4],uint256)',
                        _maxAmountsIn[0],
                        _maxAmountsIn[1],
                        _maxAmountsIn[2],
                        _maxAmountsIn[3],
                        minMintAmount
                    );
            }
        }
        if (ncoins == 5) {
            if (supportsUnderlyingParam[_poolAddress]) {
                return
                    abi.encodeWithSignature(
                        'add_liquidity(uint256[5],uint256,bool)',
                        _maxAmountsIn[0],
                        _maxAmountsIn[1],
                        _maxAmountsIn[2],
                        _maxAmountsIn[3],
                        _maxAmountsIn[4],
                        minMintAmount,
                        true
                    );
            } else {
                return
                    abi.encodeWithSignature(
                        'add_liquidity(uint256[5],uint256)',
                        _maxAmountsIn[0],
                        _maxAmountsIn[1],
                        _maxAmountsIn[2],
                        _maxAmountsIn[3],
                        _maxAmountsIn[4],
                        minMintAmount
                    );
            }
        }
        return bytes('');
    }

    function _getRemoveLiquidityMethodData(
        address _poolAddress,
        uint256 ncoins,
        uint256[] calldata _minAmountsOut,
        uint256 _poolTokensIn
    ) private view returns (bytes memory) {
        // For meta remove everything in the stable coin
        if (curveMetaRegistry.isMeta(_poolAddress) || _poolAddress == cvxCRVPool) {
            return
                abi.encodeWithSignature(
                    'remove_liquidity_one_coin(uint256,int128,uint256)',
                    _poolTokensIn,
                    int128(0),
                    _minAmountsOut[0]
                );
        }
        if (ncoins == 2) {
            if (supportsUnderlyingParam[_poolAddress]) {
                return
                    abi.encodeWithSignature(
                        'remove_liquidity(uint256,uint256[2],bool)',
                        _poolTokensIn,
                        _minAmountsOut[0],
                        _minAmountsOut[1],
                        true
                    );
            } else {
                if (_poolAddress == palstkaave) {
                    return
                        abi.encodeWithSignature(
                            'remove_liquidity_one_coin(uint256,uint256,uint256)',
                            _poolTokensIn,
                            uint256(0),
                            _minAmountsOut[0]
                        );
                }
                return
                    abi.encodeWithSignature(
                        'remove_liquidity(uint256,uint256[2])',
                        _poolTokensIn,
                        _minAmountsOut[0],
                        _minAmountsOut[1]
                    );
            }
        }
        if (ncoins == 3) {
            if (supportsUnderlyingParam[_poolAddress]) {
                return
                    abi.encodeWithSignature(
                        'remove_liquidity(uint256,uint256[3],bool)',
                        _poolTokensIn,
                        _minAmountsOut[0],
                        _minAmountsOut[1],
                        _minAmountsOut[2],
                        true
                    );
            } else {
                return
                    abi.encodeWithSignature(
                        'remove_liquidity(uint256,uint256[3])',
                        _poolTokensIn,
                        _minAmountsOut[0],
                        _minAmountsOut[1],
                        _minAmountsOut[2]
                    );
            }
        }
        if (ncoins == 4) {
            if (supportsUnderlyingParam[_poolAddress]) {
                return
                    abi.encodeWithSignature(
                        'remove_liquidity(uint256,uint256[4],bool)',
                        _poolTokensIn,
                        _minAmountsOut[0],
                        _minAmountsOut[1],
                        _minAmountsOut[2],
                        _minAmountsOut[3],
                        true
                    );
            } else {
                return
                    abi.encodeWithSignature(
                        'remove_liquidity(uint256,uint256[4])',
                        _poolTokensIn,
                        _minAmountsOut[0],
                        _minAmountsOut[1],
                        _minAmountsOut[2],
                        _minAmountsOut[3]
                    );
            }
        }
        if (ncoins == 5) {
            if (supportsUnderlyingParam[_poolAddress]) {
                return
                    abi.encodeWithSignature(
                        'remove_liquidity(uint256,uint256[5],bool)',
                        _poolTokensIn,
                        _minAmountsOut[0],
                        _minAmountsOut[1],
                        _minAmountsOut[3],
                        _minAmountsOut[4],
                        true
                    );
            } else {
                return
                    abi.encodeWithSignature(
                        'remove_liquidity(uint256,uint256[5])',
                        _poolTokensIn,
                        _minAmountsOut[0],
                        _minAmountsOut[1],
                        _minAmountsOut[2],
                        _minAmountsOut[3],
                        _minAmountsOut[4]
                    );
            }
        }
        return bytes('');
    }

    function _getLpToken(address _pool) internal view override returns (address) {
        // For Deposits & stable swaps that support it get the LP token, otherwise get the pool
        return curveMetaRegistry.getLpToken(_pool);
    }

    function _getUnderlyingAndRate(bytes calldata _pool, uint256 _i) internal view override returns (address, uint256) {
        address poolAddress = BytesLib.decodeOpDataAddress(_pool);
        return curveMetaRegistry.getUnderlyingAndRate(poolAddress, _i);
    }

    function _getRewardTokens(
        address /* _pool */
    ) internal pure override returns (address[] memory) {
        address[] memory rewards = new address[](2);
        rewards[0] = CRV;
        rewards[1] = CVX;
        return rewards;
    }
}

contracts/integrations/trade/CurveTradeI.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity 0.7.6;
pragma abicoder v2;

import {IBabController} from '../../interfaces/IBabController.sol';
import {IStrategy} from '../../interfaces/IStrategy.sol';
import {ICurveMetaRegistry} from '../../interfaces/ICurveMetaRegistry.sol';

import {LowGasSafeMath as SafeMath} from '../../lib/LowGasSafeMath.sol';
import {ControllerLib} from '../../lib/ControllerLib.sol';

import {TradeIntegration} from './TradeIntegration.sol';

/**
 * @title CurveTradeIntegration
 * @author Babylon Finance Protocol
 *
 * Curve trade integration
 */
contract CurveTradeIntegration is TradeIntegration {
    using SafeMath for uint256;
    using ControllerLib for IBabController;

    /* ============ Modifiers ============ */

    /* ============ State Variables ============ */

    /* ============ Constants ============ */

    ICurveMetaRegistry private immutable curveMetaRegistry;

    /* ============ Constructor ============ */

    /**
     * Creates the integration
     *
     * @param _controller                   Address of the controller
     * @param _curveMetaRegistry            Address of the curve meta registry
     */
    constructor(IBabController _controller, ICurveMetaRegistry _curveMetaRegistry)
        TradeIntegration('curve_trade', _controller)
    {
        require(address(_curveMetaRegistry) != address(0), 'Address needs to be valid');
        curveMetaRegistry = _curveMetaRegistry;
    }

    /* ============ Internal Functions ============ */

    /**
     * Executes the trade through curve.
     *
     * hparam _strategy             Address of the strategy
     * @param _sendToken            Address of the token to be sent to the exchange
     * @param _sendQuantity         Units of reserve asset token sent to the exchange
     * @param _receiveToken         Address of the token that will be received from the exchange
     */
    function _getTradeCallData(
        address, /* _strategy */
        address _sendToken,
        uint256 _sendQuantity,
        address _receiveToken
    )
        internal
        view
        override
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        (address curvePool, address realSendToken, address realReceiveToken) =
            _getPoolAndTokens(_sendToken, _receiveToken);
        require(curvePool != address(0), 'No curve pool');
        (uint256 i, uint256 j, bool underlying) =
            curveMetaRegistry.getCoinIndices(curvePool, realSendToken, realReceiveToken);
        // Palstakeaave. TODO: Add others
        bytes memory methodData;
        // palStkAAVE/AAVE pool
        if (curvePool == 0x48536EC5233297C367fd0b6979B75d9270bB6B15) {
            methodData = abi.encodeWithSignature('exchange(uint256,uint256,uint256,uint256)', i, j, _sendQuantity, 1);
        } else {
            methodData = abi.encodeWithSignature(
                'exchange(int128,int128,uint256,uint256)',
                int128(i),
                int128(j),
                _sendQuantity,
                1
            );
            if (underlying) {
                methodData = abi.encodeWithSignature(
                    'exchange_underlying(int128,int128,uint256,uint256)',
                    int128(i),
                    int128(j),
                    _sendQuantity,
                    1
                );
            }
        }

        return (curvePool, realSendToken == ETH_ADD_CURVE ? _sendQuantity : 0, methodData);
    }

    /**
     * Returns the address to approve source tokens to for trading. This is the TokenTaker address
     *
     * @return address             Address of the contract to approve tokens to
     */
    function _getSpender(address _swapTarget) internal pure override returns (address) {
        return _swapTarget;
    }

    /**
     * Return pre action calldata
     *
     * @param  _sendToken               Address of the asset to send
     * hparam  _receiveToken            Address of the asset to receive
     * @param  _sendQuantity            Amount of the asset to send
     *
     * @return address                   Target contract address
     * @return uint256                   Call value
     * @return bytes                     Trade calldata
     */
    function _getPreActionCallData(
        address _sendToken,
        address _receiveToken,
        uint256 _sendQuantity
    )
        internal
        view
        override
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        // Unwrap WETH to ETH
        (, address _realSendToken, ) = _getPoolAndTokens(_sendToken, _receiveToken);
        if (_realSendToken == ETH_ADD_CURVE) {
            bytes memory methodData = abi.encodeWithSignature('withdraw(uint256)', _sendQuantity);
            return (WETH, 0, methodData);
        }
        return (address(0), 0, bytes(''));
    }

    /**
     * Return post action calldata
     *
     * hparam  _sendToken               Address of the asset to send
     * @param  _receiveToken            Address of the asset to receive
     * @param  _sendQuantity            Amount of the asset to send
     *
     * @return address                   Target contract address
     * @return uint256                   Call value
     * @return bytes                     Trade calldata
     */
    function _getPostActionCallData(
        address _sendToken,
        address _receiveToken,
        uint256 _sendQuantity
    )
        internal
        view
        override
        returns (
            address,
            uint256,
            bytes memory
        )
    {
        // Wrap ETH to WETH
        (, , address _realReceiveToken) = _getPoolAndTokens(_sendToken, _receiveToken);
        if (_realReceiveToken == ETH_ADD_CURVE) {
            bytes memory methodData = abi.encodeWithSignature('deposit()');
            return (WETH, _sendQuantity, methodData);
        }
        return (address(0), 0, bytes(''));
    }

    function _getPostActionToken(
        address /* _receiveToken */
    ) internal pure override returns (address) {
        return ETH_ADD_CURVE;
    }

    /* ============ Private Functions ============ */

    function _getPoolAndTokens(address _sendToken, address _receiveToken)
        private
        view
        returns (
            address,
            address,
            address
        )
    {
        address curvePool = curveMetaRegistry.findPoolForCoins(_sendToken, _receiveToken, 0);
        if (_sendToken == WETH && curvePool == address(0)) {
            _sendToken = ETH_ADD_CURVE;
            curvePool = curveMetaRegistry.findPoolForCoins(ETH_ADD_CURVE, _receiveToken, 0);
        }
        if (_receiveToken == WETH && curvePool == address(0)) {
            _receiveToken = ETH_ADD_CURVE;
            curvePool = curveMetaRegistry.findPoolForCoins(_sendToken, ETH_ADD_CURVE, 0);
        }
        return (curvePool, _sendToken, _receiveToken);
    }
}

contracts/CurveMetaRegistry.sol.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity 0.7.6;

import {IBabController} from './interfaces/IBabController.sol';
import {ICurveMetaRegistry} from './interfaces/ICurveMetaRegistry.sol';
import {IPriceTri} from './interfaces/external/curve/IPriceTri.sol';
import {ICurvePoolV3} from './interfaces/external/curve/ICurvePoolV3.sol';
import {ICurveAddressProvider} from './interfaces/external/curve/ICurveAddressProvider.sol';
import {ICurveRegistry} from './interfaces/external/curve/ICurveRegistry.sol';
import {IFactoryRegistry} from './interfaces/external/curve/IFactoryRegistry.sol';
import {ICryptoRegistry} from './interfaces/external/curve/ICryptoRegistry.sol';
import {ICryptoFactoryRegistry} from './interfaces/external/curve/ICryptoFactoryRegistry.sol';

import {ControllerLib} from './lib/ControllerLib.sol';

/**
 * @title CurveMetaRegistry
 * @author Babylon Finance Protocol
 *
 * Abstraction for all the different Curve registries
 */
contract CurveMetaRegistry is ICurveMetaRegistry {
    using ControllerLib for IBabController;

    /* ============ Constants ============ */

    // Address of Curve Address provider
    ICurveAddressProvider internal constant curveAddressProvider =
        ICurveAddressProvider(0x0000000022D53366457F9d5E68Ec105046FC4383);

    address private constant TRI_CURVE_POOL_2_LP = 0xc4AD29ba4B3c580e6D59105FFf484999997675Ff;

    IBabController public immutable controller;

    /* ============ State Variables ============ */

    // Registry of first party pools
    ICurveRegistry public curveRegistry;

    // Registry of user created pools
    IFactoryRegistry public factoryRegistry;

    // Registry of first party crypto pools
    ICryptoRegistry public cryptoRegistry;

    // Registry of third party crypto pools
    ICryptoFactoryRegistry public cryptoRegistryF;

    mapping(address => address) public cryptoFactoryLpTokenToPools;

    // Mapping of pool to registryId
    mapping(address => uint8) public poolToRegistry;

    // 0 means doesnt exist
    // 1 means first party normal
    // 2 means factory pools
    // 3 means crypto first party
    // 4 means crypto third party

    /* ============ Modifiers ============ */

    /* ============ Constructor ============ */

    constructor(IBabController _controller) {
        require(address(_controller) != address(0), 'Controller is not valid');
        controller = _controller;
        curveRegistry = ICurveRegistry(curveAddressProvider.get_registry());
        factoryRegistry = IFactoryRegistry(curveAddressProvider.get_address(3));
        cryptoRegistry = ICryptoRegistry(curveAddressProvider.get_address(5));
        cryptoRegistryF = ICryptoFactoryRegistry(curveAddressProvider.get_address(6));

        _updateMapping(4, ICurveRegistry(address(cryptoRegistryF)));
        _updateMapping(2, ICurveRegistry(address(factoryRegistry)));
        _updateMapping(3, ICurveRegistry(address(cryptoRegistry)));
        _updateMapping(1, curveRegistry);
    }

    /* ============ External Functions ============ */

    /**
     * Updates the mapping of pools for gas efficiency
     *
     */
    function updatePoolsList() public override {
        controller.onlyGovernanceOrEmergency();
        _updateMapping(4, ICurveRegistry(address(cryptoRegistryF)));
        _updateMapping(3, ICurveRegistry(address(cryptoRegistry)));
        _updateMapping(2, ICurveRegistry(address(factoryRegistry)));
        _updateMapping(1, curveRegistry);
    }

    /**
     * Updates the addresses of the registries themselves
     *
     */
    function updateCryptoRegistries() external override {
        controller.onlyGovernanceOrEmergency();
        curveRegistry = ICurveRegistry(curveAddressProvider.get_registry());
        factoryRegistry = IFactoryRegistry(curveAddressProvider.get_address(3));
        cryptoRegistry = ICryptoRegistry(curveAddressProvider.get_address(5));
        cryptoRegistryF = ICryptoFactoryRegistry(curveAddressProvider.get_address(6));
        updatePoolsList();
    }

    /**
     * Gets the coin addresses of a pool
     * @param _getUnderlying          Whether or not to get the underlying coins
     *
     * @return address[8]             Addresses of the pool assets. Array of size 8 (filled with 0)
     */
    function getCoinAddresses(address _pool, bool _getUnderlying) external view override returns (address[8] memory) {
        uint256 registryKind = poolToRegistry[_pool];
        if (_getUnderlying) {
            if (registryKind == 1) {
                return curveRegistry.get_underlying_coins(_pool);
            }
            if (registryKind == 2) {
                try factoryRegistry.get_underlying_coins(_pool) returns (address[8] memory coins) {
                    return coins;
                } catch {
                    // try normal. Some pools revert
                    _getUnderlying = false;
                }
            }
            if (registryKind > 2) {
                // crypto pools only have normal
                _getUnderlying = false;
            }
        }
        if (!_getUnderlying) {
            if (registryKind == 1) {
                return curveRegistry.get_coins(_pool);
            }
            if (registryKind == 2) {
                address[4] memory addressesF = factoryRegistry.get_coins(_pool);
                return [
                    addressesF[0],
                    addressesF[1],
                    addressesF[2],
                    addressesF[3],
                    address(0),
                    address(0),
                    address(0),
                    address(0)
                ];
            }
            if (registryKind == 3) {
                return cryptoRegistry.get_coins(_pool);
            }
            if (registryKind == 4) {
                address[2] memory addressesCF = cryptoRegistryF.get_coins(_pool);
                return [
                    addressesCF[0],
                    addressesCF[1],
                    address(0),
                    address(0),
                    address(0),
                    address(0),
                    address(0),
                    address(0)
                ];
            }
        }
    }

    /**
     * Gets the number of coins of a curve pool
     * @param _pool                   Pool Address
     *
     * @return uint256                Number of coins in the pool
     */
    function getNCoins(address _pool) public view override returns (uint256) {
        uint256 registryKind = poolToRegistry[_pool];
        if (registryKind == 1) {
            return curveRegistry.get_n_coins(_pool)[0];
        }
        if (registryKind == 2) {
            uint256 coins = factoryRegistry.get_n_coins(_pool);
            if (coins == 0) {
                // Try through meta
                (coins, ) = factoryRegistry.get_meta_n_coins(_pool);
            }
            return coins;
        }
        if (registryKind == 3) {
            return cryptoRegistry.get_n_coins(_pool);
        }
        // Crypto factory always two
        return 2;
    }

    /**
     * Gets the lp token from a curve pool address
     * @param _pool                   Pool Address
     *
     * @return address                Address of the lp token
     */
    function getLpToken(address _pool) external view override returns (address) {
        uint256 registryKind = poolToRegistry[_pool];
        // For Deposits & stable swaps that support it get the LP token, otherwise get the pool
        if (registryKind == 1) {
            return curveRegistry.get_lp_token(_pool);
        }
        if (registryKind == 3) {
            return cryptoRegistry.get_lp_token(_pool);
        }
        // Factory pools use the pool as the token
        if (registryKind == 2) {
            return _pool;
        }
        return cryptoRegistryF.get_token(_pool);
    }

    /**
     * Gets the pool from a curve lp token
     * @param _lpToken                Address of the lp token
     *
     * @return address                Address of the pool, 0 if it doesn't exist
     */
    function getPoolFromLpToken(address _lpToken) public view override returns (address) {
        // Factory pools use the pool as the token
        if (poolToRegistry[_lpToken] == 2) {
            return _lpToken;
        }
        address cryptoFactoryPool = cryptoFactoryLpTokenToPools[_lpToken];
        if (cryptoFactoryPool != address(0)) {
            return cryptoFactoryPool;
        }
        // For Deposits & stable swaps that support it get the LP token, otherwise get the pool
        try curveRegistry.get_pool_from_lp_token(_lpToken) returns (address pool) {
            if (pool != address(0)) {
                return pool;
            }
        } catch {}
        try cryptoRegistry.get_pool_from_lp_token(_lpToken) returns (address pool2) {
            return pool2;
        } catch {
            return address(0);
        }
    }

    /**
     * Returns whether the pool is a meta pool
     * @param _pool                   Pool Address
     *
     * @return bool                Whether the pool is a meta pool or not
     */
    function isMeta(address _pool) external view override returns (bool) {
        uint256 registryKind = poolToRegistry[_pool];
        if (registryKind != 1 && registryKind != 2) {
            return false;
        }
        if (registryKind == 1) {
            return curveRegistry.is_meta(_pool);
        }
        return factoryRegistry.is_meta(_pool);
    }

    /**
     * Returns the virtual price of an lp token from curve
     * @param _lpToken                Lp token Address
     *
     * @return uint256                Whether the pool is a meta pool or not
     */
    function getVirtualPriceFromLpToken(address _lpToken) external view override returns (uint256) {
        address pool = getPoolFromLpToken(_lpToken);
        uint256 registryKind = poolToRegistry[pool];
        // Normal pools
        if (registryKind == 1) {
            return curveRegistry.get_virtual_price_from_lp_token(_lpToken);
        }
        if (registryKind == 2) {
            // Factory registry do not have the method but pools do
            return ICurvePoolV3(pool).get_virtual_price();
        }
        // Special case tricrypto 2
        if (_lpToken == TRI_CURVE_POOL_2_LP) {
            return IPriceTri(0xE8b2989276E2Ca8FDEA2268E3551b2b4B2418950).lp_price();
        }
        // for crypto pools get directly from the pool the lp price
        try ICurvePoolV3(pool).lp_price() returns (uint256 price) {
            return price;
        } catch {
            return 0;
        }
    }

    /**
     * Returns the underlying asset and the rate of conversion at the index i
     * @param _pool                   Pool Address
     * @param _i                      Asset index
     *
     * @return address                Address of the underlying asset. 0 if none
     * @return uint256                Rate of conversion between the underlying and the asset
     */
    function getUnderlyingAndRate(address _pool, uint256 _i) external view override returns (address, uint256) {
        uint256 registryKind = poolToRegistry[_pool];

        if (registryKind == 1) {
            return (curveRegistry.get_underlying_coins(_pool)[_i], curveRegistry.get_rates(_pool)[_i]);
        }
        if (registryKind == 2) {
            return (factoryRegistry.get_underlying_coins(_pool)[_i], factoryRegistry.get_rates(_pool)[_i]);
        }
        // No underlying
        return (address(0), 0);
    }

    /**
     * Finds a pool given those tokens and the index _i
     * @param _fromToken              Token 1
     * @param _toToken                Token 2
     * @param _i                      Index of the pool to retrieve (if more than one)
     *
     * @return address                Address of the pool
     */
    function findPoolForCoins(
        address _fromToken,
        address _toToken,
        uint256 _i
    ) external view override returns (address) {
        address result = curveRegistry.find_pool_for_coins(_fromToken, _toToken, _i);
        if (result != address(0)) {
            return result;
        }
        result = factoryRegistry.find_pool_for_coins(_fromToken, _toToken, _i);
        if (result != address(0)) {
            return result;
        }
        result = cryptoRegistry.find_pool_for_coins(_fromToken, _toToken, _i);
        if (result != address(0)) {
            return result;
        }
        return cryptoRegistryF.find_pool_for_coins(_fromToken, _toToken, _i);
    }

    /**
     * Gets the indices of two tokens given a pool address
     * @param _pool                   Pool address
     * @param _fromToken              Token 1
     * @param _toToken                Token 2
     *
     * @return uint256                Index of token 1
     * @return uint256                Index of token 2
     * @return bool                   Whether or not is underlying
     */
    function getCoinIndices(
        address _pool,
        address _fromToken,
        address _toToken
    )
        external
        view
        override
        returns (
            uint256,
            uint256,
            bool
        )
    {
        uint256 registryKind = poolToRegistry[_pool];
        bool underlying = false;
        uint256 oneIndex;
        uint256 twoIndex;
        if (registryKind == 1) {
            (int128 oneIndexI, int128 twoIndexI, bool underlyingI) =
                curveRegistry.get_coin_indices(_pool, _fromToken, _toToken);
            oneIndex = uint256(oneIndexI);
            twoIndex = uint256(twoIndexI);
            underlying = underlyingI;
        }
        if (registryKind == 2) {
            (int128 oneIndexI, int128 twoIndexI, bool underlyingI) =
                factoryRegistry.get_coin_indices(_pool, _fromToken, _toToken);
            oneIndex = uint256(oneIndexI);
            twoIndex = uint256(twoIndexI);
            underlying = underlyingI;
        }
        if (registryKind == 3) {
            (oneIndex, twoIndex) = cryptoRegistry.get_coin_indices(_pool, _fromToken, _toToken);
        }
        if (registryKind == 4) {
            (oneIndex, twoIndex) = cryptoRegistryF.get_coin_indices(_pool, _fromToken, _toToken);
        }
        return (oneIndex, twoIndex, underlying);
    }

    /**
     * Returns whether or not a given address is a curve pool
     * @param _poolAddress            Pool address
     *
     * @return bool                   Whether or not is underlying
     */
    function isPool(address _poolAddress) external view override returns (bool) {
        return poolToRegistry[_poolAddress] != 0;
    }

    /* ============ Internal Functions ============ */

    // Function to the update the registry mappings
    function _updateMapping(uint8 _index, ICurveRegistry _registry) internal {
        for (uint256 i = 0; i < _registry.pool_count(); i++) {
            address pool = _registry.pool_list(i);
            poolToRegistry[pool] = _index;
            // Adds lptoken to pool for cryptofactory pools
            if (_index == 4) {
                cryptoFactoryLpTokenToPools[ICurvePoolV3(pool).token()] = pool;
            }
        }
    }
}

