interface IPoolIntegration {
    function joinPool(
        address _strategy,
        bytes calldata _pool,
        uint256 _poolTokensOut,
        address[] memory _poolTokens,
        uint256[] memory _maxAmountsIn
    ) external;

    function exitPool(
